import React from 'react';
import '../../App.css';
import './Pages.css';
import one from './1.png';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';

/* import CrimeComparison from '../CrimeComparison';
import AverageCrimeTrend from '../AverageCrimeTrend'; */

const columns = [
    { id: 'name', label: 'Name', minWidth: 170 },
    { id: 'code', label: 'ISO\u00a0Code', minWidth: 100 },
    {
      id: 'population',
      label: 'Population',
      minWidth: 170,
      align: 'right',
      format: (value) => value.toLocaleString('en-US'),
    },
    {
      id: 'size',
      label: 'Size\u00a0(km\u00b2)',
      minWidth: 170,
      align: 'right',
      format: (value) => value.toLocaleString('en-US'),
    },
    {
      id: 'density',
      label: 'Density',
      minWidth: 170,
      align: 'right',
      format: (value) => value.toFixed(2),
    },
  ];

const useStyles = makeStyles((theme) => ({
    root: {
      maxWidth: 900,
    },
    media: {
      height: 0,
      paddingTop: '56.25%', // 16:9
    },
  }));

export default function CrimeCategorization() {

    const classes = useStyles();
    
    return (
        <Grid
            container
            spacing={0}
            direction="column"
            alignItems="center"
            justify="center"
            style={{ minHeight: '100vh' }}
        >
            <Grid>
            <Card className = {classes.root}>
                <CardHeader>
                <Typography><h1>Crime Categorization</h1></Typography>
                </CardHeader>
            <CardMedia
                className={classes.media}
                image={one}
                title="Paella dish"
            />
            <CardContent>
            <Typography variant="body2" color="textSecondary" component="p">
                As seen above, this page displays the types of crimes shown as a trend over time. 
                The graph can be altered depending on the information a user wants to learn from it by adjusting the controls built into the graph panel above.
            </Typography>
            </CardContent>
            </Card>
            </Grid>
        </Grid>
        /*{<CrimeComparison />
            <AverageCrimeTrend />*/
    );
}
