import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom' ;
import './Navbar.css';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';

function MenuTabs(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
    // eslint-disable-next-line
      role="menutabs"
      hidden={value !== index}
      id={`scrollable-auto-tabpanel-${index}`}
      aria-labelledby={`scrollable-auto-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

MenuTabs.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `scrollable-auto-tab-${index}`,
    'aria-controls': `scrollable-auto-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    border: 1,
    textColor: 'white',
    textAlign: 'center',
    background: "linear-gradient(315deg, #d3d3d3 0%, #57606f 74%)",
  },
  indicator: {
    top: 0,
    padding: (0, 0, 0, 2),
    background: "linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)"
  },
  tabs: {
    textDecoration: 'none',
    color: 'white',
  },
}));

export default function ScrollableTabsButtonAuto() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div>
      <AppBar 
      className={classes.root} 
      position="static">
        <Tabs
        elevation={0}
          classes={{
            indicator: classes.indicator,
            tabs: classes.tabs
          }}
          style={{height: "32px"}}
          value={value}
          onChange={handleChange}
          variant="scrollable"
          scrollButtons="auto"
          aria-label="scrollable auto tabs example"
        >
          <Tab className={classes.tabs} label="Crime Categorization" {...a11yProps(0)} component={Link} to={"/CrimeCategorization"} />
          <Tab label="SF Safety" {...a11yProps(1)} component={Link} to={"/"}/>
          <Tab label="SF Tourism" {...a11yProps(2)} />
          <Tab label="SF Law Enforcement" {...a11yProps(3)} />
          <Tab label="SF Crime Resolution" {...a11yProps(4)} />
        </Tabs>
      </AppBar>
      <MenuTabs value={value} index={0}>
        Item One
      </MenuTabs></div>
  );
}
